def load_image(image):
    image = plt.imread(image)
    img = tf.image.convert_image_dtype(image , tf.float32)
    img = tf.image.resize(img , [400 , 400]) 
    img = img[tf.newaxis , :] # Shape -> (batch_size, h, w, d)
    return img

